const DetailHotel = () => {
  const details = require("../../../data/detail.json");
  return (
    <div>
      <DetailHeader item={details} />
      <RenderPhoto item={details.photos} />
      <div>
        <title>{details.title}</title>
      </div>
    </div>
  );
};

export default DetailHotel;
