import React from "react";
import "./SearchItem.css";
// "name": "Tower Street Apartments",
// 		"distance": "500m",
// 		"tag": "Free airport taxi",
// 		"type": "Entire studio • 1 bathroom • 21m² 1 full bed",
// 		"description": "Studio Apartment with Air conditioning",
// 		"free_cancel": true,
// 		"price": 112,
// 		"rate": 8.9,
// 		"rate_text": "Excellent",
// 		"image_url": "./images/hotel_search_1.webp"

const SearchItem = (props) => {
  const item = props.item;
  return (
    <div>
      <img src={item.image_url} alt=""></img>
      <div>
        <h3>{item.name}</h3>
        <p>{`${item.distance} from center`}</p>
        <span>{item.tag}</span>
        <p>
          <strong>{item.description}</strong>
        </p>
        <p>{item.type}</p>
        {item.free_cancel ? (
          <div>
            <p>
              <strong>Free cancellation</strong>
            </p>
            <p>You can cancel later, so lock in this great proce today!</p>
          </div>
        ) : (
          ""
        )}
      </div>
      <div>
        <div>
          <p>{item.rate_text}</p>
          <span>{item.rate}</span>
        </div>
        <div>
          <p>${item.price}</p>
          <p>Includes taxes and fees</p>
          <button>See availability</button>
        </div>
      </div>
    </div>
  );
};

export default SearchItem;
