import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./NavItem.css";

const NavItem = (props) => {
  const [active, setActive] = useState(props.item.active ? props.item : "");
  const onClickNavItemHandler = (event) => {
    props.onActive(event.target.textContent.trim());
  };
  return (
    <div
      className={`nav-item ${props.active === props.item.type ? "active" : ""}`}
      onClick={onClickNavItemHandler}
    >
      <FontAwesomeIcon icon={`fa-solid ${props.item.icon}`} /> {props.item.type}
    </div>
  );
};

export default NavItem;
