import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import React from "react";
import "./HeaderSearchBar.css";

const HeaderSearhcBar = () => {
  const goToSearch = () => {
    window.location.assign("http://localhost:3000/search");
  };
  const date = new Date().toLocaleDateString("vn-VN");

  return (
    <form className="search-bar">
      <div>
        <FontAwesomeIcon icon="fa-bed" />
        <input type="text" placeholder="Where are you going?"></input>
      </div>
      <div>
        <FontAwesomeIcon icon="fa-calendar" />
        <label></label>
      </div>
      <label>
        <FontAwesomeIcon icon="fa-person" /> 1 adult 0 children 1 room
      </label>
      <button onClick={goToSearch}>Search</button>
    </form>
  );
};

export default HeaderSearhcBar;
