import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./NavItem.css";

const NavItem = (props) => {
  const [active, setActive] = useState(props.item);
  const onClickNavItemHandler = (event) => {
    // console.log(event.target.textContent);
    const index = active.findIndex(
      (e) => e.type === event.target.textContent.trim()
    );
    console.log(index);
  };
  return (
    <div className="navbar-item">
      {active.map((navItem) => (
        <div
          key={navItem.type}
          className={`nav-item ${navItem.active ? "active" : ""}`}
          onClick={onClickNavItemHandler}
        >
          <FontAwesomeIcon icon={`fa-solid ${navItem.icon}`} /> {navItem.type}
        </div>
      ))}
    </div>
  );
};

export default NavItem;
