import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./NavItem.css";

const NavItem = (props) => {
  const [active, setActive] = useState("Stays");

  const onClickNavItemHandler = (event) => {
    setActive(event.target.textContent);
    console.log(event.target.textContent);
  };
  const classes = (active, props) => {
    return "nav-item " + props.className;
  };
  return (
    <div
      className={`${classes(active, props.type)}`}
      onClick={onClickNavItemHandler}
    >
      <FontAwesomeIcon icon={`fa-solid ${props.icon}`} /> {props.type}
    </div>
  );
};

export default NavItem;
