import React from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./NavItem.css";

const NavItem = (props) => {
  const onClickNavItemHandler = (event) => {
    props.onChangeActive(event.target.textContent);
  };
  console.log(props.itemActive);
  return (
    <div
      className={`nav-item ${props.itemActive === props.type ? "active" : ""}`}
      onClick={onClickNavItemHandler}
    >
      <FontAwesomeIcon icon={`fa-solid ${props.icon}`} /> {props.type}
    </div>
  );
};

export default NavItem;
