import React, { useState } from "react";
import BlueContainer from "../UI/BlueContainer";
import NavItem from "./NavItem";
import "./NavBar.css";
const NAVITEMS = [
  {
    type: "Stays",
    icon: "fa-bed",
    active: true,
  },
  {
    type: "Flights",
    icon: "fa-plane",
    active: false,
  },
  {
    type: "Car rentals",
    icon: "fa-car",
    active: false,
  },
  {
    type: "Attractions",
    icon: "fa-bed",
    active: false,
  },
  {
    type: "Airport taxis",
    icon: "fa-taxi",
    active: false,
  },
];
const NavBar = (props) => {
  const [navItems, setNavItems] = useState(NAVITEMS);
  const [selected, setSelected] = useState("Stays");
  const changeActiveHandler = (event) => {
    setSelected(event);
    console.log(event);
  };

  return (
    <BlueContainer>
      <div className="navbar">
        <div className="navbar-header">
          <div>Booking Website</div>
          <div>
            <button>Register</button>
            <button>Login</button>
          </div>
        </div>
        <div className="navbar-item">
          {navItems.map((navItem) => (
            <NavItem
              itemActive={selected}
              onChangeActive={changeActiveHandler}
              key={navItem.type}
              type={navItem.type}
              icon={navItem.icon}
            />
          ))}
        </div>
      </div>
    </BlueContainer>
  );
};

export default NavBar;
