import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./NavItem.css";

const NavItem = (props) => {
  const [activeNavItem, setActiveNavItem] = useState("Stays");
  let click = "Stays";
  const onClickNavItemHandler = (event) => {
    setActiveNavItem(event.target.textContent);
  };

  return (
    <div
      className={`nav-item ${props.selected === activeNavItem ? "active" : ""}`}
      onClick={onClickNavItemHandler}
    >
      <FontAwesomeIcon icon={`fa-solid ${props.icon}`} /> {props.type}
    </div>
  );
};

export default NavItem;
