import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./NavItem.css";

const NavItem = (props) => {
  const [active, setActive] = useState(props.item.active);
  console.log(props.item);
  console.log(props.item[0].type);
  const onClickNavItemHandler = (event) => {
    setActive(true);
  };

  return (
    <div>
      $
      {props.item.map((navItem) => (
        <div
          className={`nav-item ${navItem.active ? "active" : ""}`}
          onClick={onClickNavItemHandler}
        >
          <FontAwesomeIcon icon={`fa-solid ${navItem.icon}`} /> {navItem.type}
        </div>
      ))}
    </div>
  );
};

export default NavItem;
