import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./NavItem.css";

const NavItem = (props) => {
  const [active, setActive] = useState(props.item);
  const onClickNavItemHandler = (event) => {
    // console.log(event.target.textContent);
    const index = active.findIndex(
      (e) => e.type === event.target.textContent.trim()
    );
    setActive();
  };
  return (
    <div
      className={`nav-item ${active.active ? "active" : ""}`}
      onClick={onClickNavItemHandler}
    >
      <FontAwesomeIcon icon={`fa-solid ${active.icon}`} /> {active.type}
    </div>
  );
};

export default NavItem;
